import React, { useState } from 'react'
import './App.css';
import NIFTILogo from './images/NIFTISmallLogo.png';

function App() {
  const [visible, setVisible] = useState(false);
  const [fadeInOut, setFadeInOut] = useState(true);

  return (
  <div className='qrcode-container'>
      <div className='qrcode'>
          <Fade in={fadeInOut} tag='QRCodeSVG'>
              {visible && <QRCodeSVG
              value={"https://playvalorant.com/ko-kr/"}
              size={180}
              bgColor={"#ffffff"}
              fgColor={"#0090ff"}
              level={"L"}
              includeMargin={false}
              imageSettings={{
                  src: NIFTILogo,
                  x: undefined,
                  y: undefined,
                  height: 40,
                  width: 40,
                  excavate: true,
              }}
              />
              }
          </Fade>
      </div>
      <Button className='qr-button' onClick={() => {
          setVisible(!visible); 
          setFadeInOut(!fadeInOut);
      }} color='#0091ff'>
          {visible ? 'check it!' : 'generate'}
      </Button>
  </div>
  );
}

export default App;
